detail_find_rule:
js:
var d = []; //自定义一个 空数组
var html = getResCode();
var lbb_a = pdfa(html, 'body&&.article-content&&a');

标题 = pdfh(html, 'h2&&Text')
文章 = pdfh(html, '.article-content&&Html')

链接列表 = 文章.match(/".*?www\.aliyundrive\.com.*?"/g)
for (var i in 链接列表) {
    d.push({
        title: "““云盘君.简””",
        url: 'hiker://page/aliyun?rule=云盘君.简&realurl=' + 链接列表[i].replace(/"/g, ""),
        col_type: 链接列表.length > 1 ? "text_2" : "text_center_1"
    });

}
文章 = "<body>" + "<h2>" + 标题 + "</h2>" + 文章 + "</body>"

d.push({
    title: 文章.replace(/\,/g, '').replace(/<a href=".*?aliyundrive\.com/g, '<a href="hiker://page/detail?rule=云盘汇影&url=https://www.aliyundrive.com'),
    col_type: "rich_text"
});

setResult(d);
